screen.onload:
window.selectView = function(tab) {
  applyProperty("Chart1","visibility","hidden")
  applyProperty("Chart2","visibility","hidden")
  applyProperty("ImagePanel","visibility","hidden")
  applyProperty("Map1","visibility","hidden")
  switch(tab){
    case 0:
      applyProperty("ImagePanel","visibility","visible")
      break;
    case 1:
      applyProperty("Map1","visibility","visible")
      break;
    case 2:
      applyProperty("Chart1","visibility","visible")
      break;
    case 3:
      applyProperty("Chart2","visibility","visible")
  }

}

let tab = Number(getObj("Layout4").pui.properties["active tab"]) || 0
window.selectView(tab)
