let map = getObj("Map1");
map.updateData = function(){
  let deliveries = get("OnOffSwitch1");
  let records = getObj("mainsfl").grid.getAllDataValues(true);
  window.data.data = [];
  records.forEach((r) => { 
  let rec = window.data.data.find(d => d.id == (deliveries ? r.tostate : r.fromstate ));
    if(rec) {
    rec.value += 1; }
    else {
    window.data.data.push({id:(deliveries ? r.tostate : r.fromstate ),value:1});}  
  });
  let fc = FusionCharts("Map1_Chart");
  fc.setChartData(window.data,'json');
}

window.filterGrid = function(column,value){
  getObj("mainsfl").grid.setFilter(column, value);
}

map.updateData();
let fcMap = FusionCharts("Map1_Chart");
if(fcMap) fcMap.addEventListener("entityClick", (eventObj, dataObj) => window.filterGrid((get("OnOffSwitch1") ? 6 : 4), dataObj['originalId']));

let fc = FusionCharts("Chart1_Chart");
